  return (
    <div>
      <img className="small" src={product.image} alt={product.name} />
      <h3>{product.name}</h3>
      <div>${product.price}</div>
      <div>
        <button onClick={()=>{
          onAdd(product)

        localStorage.setItem("bagdata",JSON.stringify(product))
        }}>Add To Cart</button>
      </div>
    </div>
  );
}
